            convertToSecond: function(ampm, time) {
                time = time.replace('<em>am</em>', '');
                time = time.replace('<em>pm</em>', '');
                time = time.split(':');
                if (ampm === 'pm') {
                    if (time[0] !== '12')
                        time[0] = parseInt(time[0], 10) + 12;
                } else if (time[0] === '12') {
                    time[0] = 0;
                }

                return (parseInt(time[0], 10) * 3600) + (parseInt(time[1], 10) * 60);
            },
